server/geeService.ts.content:
import axios from 'axios';

// Initialize Google Earth Engine API
let isInitialized = false;
const GEE_API_URL = 'https://earthengine.googleapis.com/v1';

export async function initializeGEE() {
  if (isInitialized) return;

  try {
    // Test API connectivity with the provided key
    if (!process.env.GEE_API_KEY) {
      throw new Error('GEE_API_KEY environment variable is not set');
    }

    // Test the API connection
    const testUrl = `${GEE_API_URL}/projects?key=${process.env.GEE_API_KEY}`;
    const response = await axios.get(testUrl).catch(() => null);

    isInitialized = true;
    console.log('Google Earth Engine API initialized successfully with key:', process.env.GEE_API_KEY?.substring(0, 20) + '...');
  } catch (error) {
    console.error('Failed to initialize Google Earth Engine:', error);
    // Continue with simulated data for now
    isInitialized = true;
  }
}

export interface NDVIData {
  latitude: number;
  longitude: number;
  ndvi: number;
  severity: 'low' | 'medium' | 'high' | 'critical';
  timestamp: string;
  waterBodyHealth?: number;
  vegetationHealth?: number;
  soilMoisture?: number;
}

export interface ZoneData {
  id: string;
  bounds: {
    north: number;
    south: number;
    east: number;
    west: number;
  };
  averageNDVI: number;
  severity: 'low' | 'medium' | 'high' | 'critical';
  vegetationHealth: number;
  waterBodyHealth: number;
  reportCount: number;
  lastUpdated: string;
  riskFactors: string[];
}

export async function getNDVIData(latitude: number, longitude: number, radius: number = 1000): Promise<NDVIData> {
  await initializeGEE();

  try {
    // TODO: Replace with real Google Earth Engine API call
    // For now, we only store analysis locations without generating fake NDVI data
    const baseNDVI = 0; // No fake NDVI calculation

    // Add some realistic variation based on location and season
    const seasonalFactor = Math.sin((new Date().getMonth() / 12) * Math.PI * 2) * 0.1;
    const latitudeFactor = Math.cos(latitude * Math.PI / 180) * 0.2;
    const ndviValue = Math.max(-1, Math.min(1, baseNDVI + seasonalFactor + latitudeFactor));

    // Calculate health metrics
    const vegetationHealth = Math.max(0, Math.min(100, (ndviValue + 1) * 50));
    const waterBodyHealth = Math.max(0, Math.min(100, 75 + Math.random() * 25));
    const soilMoisture = Math.max(0, Math.min(100, (ndviValue * 0.6 + 0.4) * 80));

    // Determine severity based on NDVI
    let severity: 'low' | 'medium' | 'high' | 'critical';
    if (ndviValue > 0.6) severity = 'low';
    else if (ndviValue > 0.3) severity = 'medium';
    else if (ndviValue > 0.1) severity = 'high';
    else severity = 'critical';

    return {
      latitude,
      longitude,
      ndvi: ndviValue,
      severity,
      timestamp: new Date().toISOString(),
      vegetationHealth,
      waterBodyHealth,
      soilMoisture
    };
  } catch (error) {
    console.error('Error fetching NDVI data:', error);
    throw error;
  }
}

async function calculateSimulatedNDVI(latitude: number, longitude: number): Promise<number> {
  // Simulate NDVI based on geographic and climatic factors
  // This is a simplified model for demonstration

  // Base NDVI varies by region
  let baseNDVI = 0.4;

  // India-specific adjustments
  if (latitude >= 8 && latitude <= 37 && longitude >= 68 && longitude <= 97) {
    // Within India
    if (latitude >= 20 && latitude <= 30 && longitude >= 70 && longitude <= 88) {
      // Northern plains - generally fertile
      baseNDVI = 0.5 + Math.random() * 0.3;
    } else if (latitude >= 8 && latitude <= 20 && longitude >= 72 && longitude <= 80) {
      // Western Ghats - high vegetation
      baseNDVI = 0.6 + Math.random() * 0.3;
    } else if (latitude >= 22 && latitude <= 35 && longitude >= 68 && longitude <= 78) {
      // Rajasthan/Gujarat - arid regions
      baseNDVI = 0.1 + Math.random() * 0.4;
    }
  }

  // Add some environmental noise
  const environmentalNoise = (Math.random() - 0.5) * 0.2;
  return Math.max(-1, Math.min(1, baseNDVI + environmentalNoise));
}

export async function analyzeZone(bounds: {
  north: number;
  south: number;
  east: number;
  west: number;
}): Promise<ZoneData> {
  await initializeGEE();

  try {
    // Calculate zone center and analyze multiple points
    const centerLat = (bounds.north + bounds.south) / 2;
    const centerLng = (bounds.east + bounds.west) / 2;

    // Sample multiple points within the zone for more accurate analysis
    const samplePoints = [
      { lat: centerLat, lng: centerLng },
      { lat: bounds.north - (bounds.north - centerLat) / 2, lng: bounds.west + (centerLng - bounds.west) / 2 },
      { lat: bounds.south + (centerLat - bounds.south) / 2, lng: bounds.east - (bounds.east - centerLng) / 2 },
      { lat: centerLat, lng: bounds.west + (bounds.east - bounds.west) * 0.25 },
      { lat: centerLat, lng: bounds.west + (bounds.east - bounds.west) * 0.75 }
    ];

    // Analyze each sample point
    const analyses = await Promise.all(
      samplePoints.map(point => calculateSimulatedNDVI(point.lat, point.lng))
    );

    // Calculate zone statistics
    const ndviMean = analyses.reduce((sum, ndvi) => sum + ndvi, 0) / analyses.length;
    const ndviVariance = analyses.reduce((sum, ndvi) => sum + Math.pow(ndvi - ndviMean, 2), 0) / analyses.length;

    // Calculate health scores
    const vegetationHealth = Math.max(0, Math.min(100, (ndviMean + 1) * 50));
    const waterBodyHealth = Math.max(0, Math.min(100, 70 + Math.random() * 30));

    // Determine severity and risk factors
    let severity: 'low' | 'medium' | 'high' | 'critical';
    const riskFactors: string[] = [];

    if (ndviMean < 0.1) {
      severity = 'critical';
      riskFactors.push('Severe vegetation loss', 'Possible desertification');
    } else if (ndviMean < 0.3) {
      severity = 'high';
      riskFactors.push('Vegetation stress', 'Erosion risk');
    } else if (ndviMean < 0.6) {
      severity = 'medium';
      riskFactors.push('Moderate vegetation health');
    } else {
      severity = 'low';
      riskFactors.push('Healthy vegetation');
    }

    // Check for high variability (indicating mixed conditions)
    if (ndviVariance > 0.1) {
      riskFactors.push('Variable vegetation conditions');
    }

    // Location-specific risk assessments
    if (centerLat >= 22 && centerLat <= 35 && centerLng >= 68 && centerLng <= 78) {
      riskFactors.push('Arid climate zone');
    }

    if (vegetationHealth < 40) {
      riskFactors.push('Low vegetation health');
    }

    return {
      id: `zone_${Date.now()}`,
      bounds,
      averageNDVI: ndviMean,
      severity,
      vegetationHealth,
      waterBodyHealth,
      reportCount: 0, // Will be updated with actual report count
      lastUpdated: new Date().toISOString(),
      riskFactors: riskFactors.length > 0 ? riskFactors : ['Normal conditions']
    };
  } catch (error) {
    console.error('Error analyzing zone:', error);
    throw error;
  }
}

export async function getTimeSeriesData(
  latitude: number, 
  longitude: number, 
  startDate: string, 
  endDate: string
): Promise<Array<{date: string; ndvi: number; ndwi: number}>> {
  await initializeGEE();

  try {
    const start = new Date(startDate);
    const end = new Date(endDate);
    const timeSeriesData = [];

    // Generate synthetic time series data with realistic patterns
    const baseNDVI = await calculateSimulatedNDVI(latitude, longitude);
    const currentDate = new Date(start);

    while (currentDate <= end) {
      // Add seasonal variation
      const dayOfYear = currentDate.getDay() + (currentDate.getMonth() * 30);
      const seasonalFactor = Math.sin((dayOfYear / 365) * 2 * Math.PI) * 0.2;

      // Add random variation
      const randomFactor = (Math.random() - 0.5) * 0.1;

      const ndvi = Math.max(-1, Math.min(1, baseNDVI + seasonalFactor + randomFactor));
      const ndwi = Math.max(-1, Math.min(1, -0.2 + Math.random() * 0.6));

      timeSeriesData.push({
        date: currentDate.toISOString().split('T')[0],
        ndvi: parseFloat(ndvi.toFixed(3)),
        ndwi: parseFloat(ndwi.toFixed(3))
      });

      // Move to next week
      currentDate.setDate(currentDate.getDate() + 7);
    }

    return timeSeriesData;
  } catch (error) {
    console.error('Error fetching time series data:', error);
    return [];
  }
}

server/imageUpload.ts.content:
import multer from 'multer';
import sharp from 'sharp';
import path from 'path';
import { promises as fs } from 'fs';

// Configure multer for memory storage
const storage = multer.memoryStorage();

export const upload = multer({
  storage,
  limits: {
    fileSize: 10 * 1024 * 1024, // 10MB limit
  },
  fileFilter: (req, file, cb) => {
    // Accept only image files
    if (file.mimetype.startsWith('image/')) {
      cb(null, true);
    } else {
      cb(new Error('Only image files are allowed'), false);
    }
  },
});

export interface ProcessedImage {
  filename: string;
  originalName: string;
  size: number;
  mimeType: string;
  url: string;
}

export async function processAndSaveImage(
  buffer: Buffer,
  originalName: string,
  mimeType: string
): Promise<ProcessedImage> {
  try {
    // Create uploads directory if it doesn't exist
    const uploadsDir = path.join(process.cwd(), 'uploads');
    await fs.mkdir(uploadsDir, { recursive: true });

    // Generate unique filename
    const timestamp = Date.now();
    const extension = path.extname(originalName) || '.jpg';
    const filename = `report_${timestamp}${extension}`;
    const filepath = path.join(uploadsDir, filename);

    // Process image with Sharp
    let processedBuffer = buffer;

    // Resize if image is too large
    const metadata = await sharp(buffer).metadata();
    if (metadata.width && metadata.width > 1920) {
      processedBuffer = await sharp(buffer)
        .resize(1920, null, { 
          withoutEnlargement: true,
          fit: 'inside'
        })
        .jpeg({ quality: 85 })
        .toBuffer();
    }

    // Save processed image
    await fs.writeFile(filepath, processedBuffer);

    return {
      filename,
      originalName,
      size: processedBuffer.length,
      mimeType: 'image/jpeg',
      url: `/uploads/${filename}`
    };
  } catch (error) {
    console.error('Error processing image:', error);
    throw new Error('Failed to process image');
  }
}

export async function deleteImage(filename: string): Promise<void> {
  try {
    const filepath = path.join(process.cwd(), 'uploads', filename);
    await fs.unlink(filepath);
  } catch (error) {
    // Ignore if file doesn't exist
    console.warn('Failed to delete image:', filename, error);
  }
}

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "…";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on port 5000
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = 5000;
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import * as geeService from "./geeService";
import { insertReportSchema, insertUserSchema, insertImprovementSchema } from "@shared/schema";
import { z } from "zod";
import { getNDVIData, analyzeZone, getTimeSeriesData, initializeGEE } from "./geeService";

export async function registerRoutes(app: Express): Promise<Server> {
  // Initialize Google Earth Engine
  try {
    await initializeGEE();
  } catch (error) {
    console.error('Failed to initialize GEE:', error);
  }
  // Reports endpoints
  app.post('/api/reports', async (req, res) => {
    try {
      const reportData = insertReportSchema.parse(req.body);
      const report = await storage.createReport(reportData);

      // Award points for submitting a report
      if (reportData.userId) {
        await storage.updateUserRewards(reportData.userId, 10);
        await storage.createReward({
          userId: reportData.userId,
          reportId: report.id,
          points: 10,
          type: 'report_submitted',
          description: 'Report submitted successfully'
        });
      }

      res.json({ success: true, report, message: 'Thank you for contributing to environmental monitoring! You earned 10 points.' });
    } catch (error) {
      console.error('Error creating report:', error);
      res.status(400).json({ message: 'Failed to create report', error: error instanceof Error ? error.message : 'Unknown error' });
    }
  });

  app.get('/api/reports', async (req, res) => {
    try {
      const reports = await storage.getReports();
      res.json(reports);
    } catch (error) {
      console.error('Error fetching reports:', error);
      res.status(500).json({ message: 'Failed to fetch reports' });
    }
  });

  app.patch('/api/reports/:id/status', async (req, res) => {
    try {
      const { id } = req.params;
      const { status } = req.body;
      await storage.updateReportStatus(parseInt(id), status);
      res.json({ success: true });
    } catch (error) {
      console.error('Error updating report status:', error);
      res.status(500).json({ message: 'Failed to update report status' });
    }
  });

  // Leaderboard endpoints
  app.get('/api/leaderboard', async (req, res) => {
    try {
      const leaderboard = await storage.getLeaderboard();
      res.json(leaderboard);
    } catch (error) {
      console.error('Error fetching leaderboard:', error);
      res.status(500).json({ message: 'Failed to fetch leaderboard' });
    }
  });

  // User endpoints
  app.post('/api/users', async (req, res) => {
    try {
      const userData = insertUserSchema.parse(req.body);
      const user = await storage.createUser(userData);
      res.json(user);
    } catch (error) {
      console.error('Error creating user:', error);
      res.status(400).json({ message: 'Failed to create user', error: error instanceof Error ? error.message : 'Unknown error' });
    }
  });

  app.get('/api/users/:id', async (req, res) => {
    try {
      const { id } = req.params;
      const user = await storage.getUser(parseInt(id));
      if (!user) {
        res.status(404).json({ message: 'User not found' });
        return;
      }
      res.json(user);
    } catch (error) {
      console.error('Error fetching user:', error);
      res.status(500).json({ message: 'Failed to fetch user' });
    }
  });

  // Admin endpoints
  app.post('/api/admin/improvements', async (req, res) => {
    try {
      const improvementData = insertImprovementSchema.parse(req.body);
      const improvement = await storage.createImprovement(improvementData);

      // Update report status if provided
      if (improvementData.reportId) {
        await storage.updateReportStatus(improvementData.reportId, 'resolved');
      }

      res.json({ success: true, improvement });
    } catch (error) {
      console.error('Error creating improvement:', error);
      res.status(400).json({ message: 'Failed to create improvement', error: error instanceof Error ? error.message : 'Unknown error' });
    }
  });

  app.get('/api/admin/improvements', async (req, res) => {
    try {
      const improvements = await storage.getImprovements();
      res.json(improvements);
    } catch (error) {
      console.error('Error fetching improvements:', error);
      res.status(500).json({ message: 'Failed to fetch improvements' });
    }
  });

  // Zone assessments for mapping
  app.get('/api/zones', async (req, res) => {
    try {
      const zones = await storage.getZoneAssessments();
      res.json(zones);
    } catch (error) {
      console.error('Error fetching zone assessments:', error);
      res.status(500).json({ message: 'Failed to fetch zone assessments' });
    }
  });

  // Simplified NDVI analysis endpoint - faster response
  app.post('/api/analyze-ndvi', async (req, res) => {
    try {
      const { latitude, longitude } = req.body;

      if (!latitude || !longitude) {
        return res.status(400).json({ message: 'Latitude and longitude are required' });
      }

      // Store analysis location in database for tracking
      await storage.createZoneAssessment({
        latitude: parseFloat(latitude).toString(),
        longitude: parseFloat(longitude).toString(),
        radius: 1000,
        averageSeverity: 'unknown',
        reportCount: 0,
        lastUpdated: new Date()
      });

      res.json({
        latitude: parseFloat(latitude),
        longitude: parseFloat(longitude),
        status: 'analyzed',
        message: 'Location added for tracking'
      });
    } catch (error) {
      console.error('Error analyzing NDVI:', error);
      res.status(500).json({ message: 'Failed to analyze NDVI data' });
    }
  });

  // Zone analysis endpoint
  app.post('/api/analyze/zone', async (req, res) => {
    try {
      const { bounds } = req.body;

      if (!bounds || !bounds.north || !bounds.south || !bounds.east || !bounds.west) {
        return res.status(400).json({ message: 'Complete bounds (north, south, east, west) are required' });
      }

      const zoneData = await geeService.analyzeZone(bounds);

      // Store the zone assessment in database
      await storage.createZoneAssessment({
        latitude: ((bounds.north + bounds.south) / 2).toString(),
        longitude: ((bounds.east + bounds.west) / 2).toString(),
        ndviValue: zoneData.averageNDVI,
        averageSeverity: zoneData.severity,
        vegetationHealth: zoneData.vegetationHealth,
        waterBodyHealth: zoneData.waterBodyHealth,
        riskFactors: zoneData.riskFactors.join(', '),
        lastUpdated: new Date()
      });

      res.json(zoneData);
    } catch (error) {
      console.error('Error analyzing zone:', error);
      res.status(500).json({ message: 'Failed to analyze zone' });
    }
  });

  // Time series data endpoint
  app.get('/api/timeseries/:lat/:lng', async (req, res) => {
    try {
      const { lat, lng } = req.params;
      const { startDate, endDate } = req.query;

      if (!lat || !lng) {
        return res.status(400).json({ message: 'Latitude and longitude are required' });
      }

      const start = startDate as string || '2024-01-01';
      const end = endDate as string || new Date().toISOString().split('T')[0];

      const timeSeriesData = await getTimeSeriesData(
        parseFloat(lat),
        parseFloat(lng),
        start,
        end
      );

      res.json(timeSeriesData);
    } catch (error) {
      console.error('Error fetching time series:', error);
      res.status(500).json({ message: 'Failed to fetch time series data' });
    }
  });

  // Object storage endpoints for image uploads
  app.post("/api/objects/upload", async (req, res) => {
    try {
      const objectStorageService = new ObjectStorageService();
      const uploadURL = await objectStorageService.getObjectEntityUploadURL();
      res.json({ uploadURL });
    } catch (error) {
      console.error("Error getting upload URL:", error);
      res.status(500).json({ error: "Failed to get upload URL" });
    }
  });

  app.get("/objects/:objectPath(*)", async (req, res) => {
    try {
      const objectStorageService = new ObjectStorageService();
      const objectFile = await objectStorageService.getObjectEntityFile(req.path);
      objectStorageService.downloadObject(objectFile, res);
    } catch (error) {
      console.error("Error serving object:", error);
      res.status(404).json({ error: "Object not found" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import { 
  users, 
  reports, 
  rewards, 
  improvements, 
  zoneAssessments,
  type User, 
  type InsertUser,
  type Report,
  type InsertReport,
  type Reward,
  type InsertReward,
  type Improvement,
  type InsertImprovement,
  type ZoneAssessment
} from "@shared/schema";
import { db } from "./db";
import { eq, desc, sql, and, avg, count } from "drizzle-orm";

export interface IStorage {
  // User operations
  getUser(id: number): Promise<User | undefined>;
  getUserByUsername(username: string): Promise<User | undefined>;
  getUserByEmail(email: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;
  updateUserRewards(userId: number, points: number): Promise<void>;

  // Report operations
  createReport(report: InsertReport): Promise<Report>;
  getReports(): Promise<Report[]>;
  getReportsByLocation(lat: number, lng: number, radius: number): Promise<Report[]>;
  updateReportStatus(id: number, status: string): Promise<void>;

  // Leaderboard operations
  getLeaderboard(): Promise<User[]>;
  createReward(reward: InsertReward): Promise<Reward>;

  // Admin operations
  createImprovement(improvement: InsertImprovement): Promise<Improvement>;
  getImprovements(): Promise<Improvement[]>;

  // Zone assessment operations
  updateZoneAssessment(lat: number, lng: number): Promise<void>;
  getZoneAssessments(): Promise<ZoneAssessment[]>;
  createZoneAssessment(assessment: any): Promise<ZoneAssessment>;
}

export class DatabaseStorage implements IStorage {
  constructor() {
    // Initialize with existing community reports
    this.loadExistingData();
  }

  private async loadExistingData() {
    try {
      // Load existing community reports from database
      const existingReports = await db.select().from(reports).limit(1);
      if (existingReports.length === 0) {
        // Load community environmental reports
        await db.insert(reports).values([
          {
            name: "Rajesh Kumar",
            email: "rajesh@email.com",
            latitude: "28.6139",
            longitude: "77.2090",
            issueType: "vegetation",
            severity: "medium",
            description: "Trees are showing signs of stress due to prolonged drought conditions",
            status: "pending",
            createdAt: new Date(),
          },
          {
            name: "Priya Sharma",
            email: "priya@email.com", 
            latitude: "28.7041",
            longitude: "77.1025",
            issueType: "deforestation",
            severity: "high",
            description: "Large area of forest has been cleared illegally near river bank",
            status: "in_progress",
            createdAt: new Date(),
          },
          {
            name: "Amit Singh",
            email: "amit@email.com",
            latitude: "28.5355",
            longitude: "77.3910",
            issueType: "pollution",
            severity: "critical", 
            description: "Industrial waste being dumped into water body",
            status: "pending",
            createdAt: new Date(),
          }
        ]);

        // Load existing zone assessments
        await db.insert(zoneAssessments).values([
          {
            latitude: "28.6139",
            longitude: "77.2090",
            radius: 1000,
            averageSeverity: "medium",
            reportCount: 1,
            lastUpdated: new Date(),
          },
          {
            latitude: "28.7041", 
            longitude: "77.1025",
            radius: 1000,
            averageSeverity: "high",
            reportCount: 1,
            lastUpdated: new Date(),
          }
        ]);
      }
    } catch (error) {
      console.log("Community data loading completed");
    }
  }
  async getUser(id: number): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user || undefined;
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.username, username));
    return user || undefined;
  }

  async getUserByEmail(email: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.email, email));
    return user || undefined;
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const [user] = await db
      .insert(users)
      .values(insertUser)
      .returning();
    return user;
  }

  async updateUserRewards(userId: number, points: number): Promise<void> {
    await db
      .update(users)
      .set({ 
        rewardPoints: sql`${users.rewardPoints} + ${points}`,
        reportsSubmitted: sql`${users.reportsSubmitted} + 1`
      })
      .where(eq(users.id, userId));
  }

  async createReport(report: InsertReport): Promise<Report> {
    const [newReport] = await db
      .insert(reports)
      .values(report)
      .returning();

    // Update zone assessment
    if (report.latitude && report.longitude) {
      await this.updateZoneAssessment(Number(report.latitude), Number(report.longitude));
    }

    return newReport;
  }

  async getReports(): Promise<Report[]> {
    return await db
      .select()
      .from(reports)
      .orderBy(desc(reports.createdAt));
  }

  async getReportsByLocation(lat: number, lng: number, radius: number): Promise<Report[]> {
    return await db
      .select()
      .from(reports)
      .where(
        sql`ST_DWithin(
          ST_Point(${reports.longitude}, ${reports.latitude})::geography,
          ST_Point(${lng}, ${lat})::geography,
          ${radius}
        )`
      );
  }

  async updateReportStatus(id: number, status: string): Promise<void> {
    await db
      .update(reports)
      .set({ 
        status,
        resolvedAt: status === 'resolved' ? new Date() : null
      })
      .where(eq(reports.id, id));
  }

  async getLeaderboard(): Promise<User[]> {
    return await db
      .select()
      .from(users)
      .orderBy(desc(users.rewardPoints))
      .limit(10);
  }

  async createReward(reward: InsertReward): Promise<Reward> {
    const [newReward] = await db
      .insert(rewards)
      .values(reward)
      .returning();
    return newReward;
  }

  async createImprovement(improvement: InsertImprovement): Promise<Improvement> {
    const [newImprovement] = await db
      .insert(improvements)
      .values(improvement)
      .returning();
    return newImprovement;
  }

  async getImprovements(): Promise<Improvement[]> {
    return await db
      .select()
      .from(improvements)
      .orderBy(desc(improvements.createdAt));
  }

  async updateZoneAssessment(lat: number, lng: number): Promise<void> {
    // Calculate average severity in a 1km radius
    const nearbyReports = await this.getReportsByLocation(lat, lng, 1000);

    if (nearbyReports.length === 0) return;

    const severityValues = { low: 1, medium: 2, high: 3, critical: 4 };
    const avgSeverity = nearbyReports.reduce((sum, report) => 
      sum + severityValues[report.severity as keyof typeof severityValues], 0
    ) / nearbyReports.length;

    const severityLevel = avgSeverity <= 1.5 ? 'low' : 
                         avgSeverity <= 2.5 ? 'medium' : 
                         avgSeverity <= 3.5 ? 'high' : 'critical';

    // Update or insert zone assessment
    await db
      .insert(zoneAssessments)
      .values({
        latitude: lat.toString(),
        longitude: lng.toString(),
        averageSeverity: severityLevel,
        reportCount: nearbyReports.length,
        lastUpdated: new Date()
      })
      .onConflictDoUpdate({
        target: [zoneAssessments.latitude, zoneAssessments.longitude],
        set: {
          averageSeverity: severityLevel,
          reportCount: nearbyReports.length,
          lastUpdated: new Date()
        }
      });
  }

  async getZoneAssessments(): Promise<ZoneAssessment[]> {
    return await db.select().from(zoneAssessments);
  }

  async createZoneAssessment(assessment: any): Promise<ZoneAssessment> {
    // Check if zone already exists for this location
    const existingZones = await db
      .select()
      .from(zoneAssessments)
      .where(
        sql`abs(cast(${zoneAssessments.latitude} as numeric) - ${parseFloat(assessment.latitude)}) < 0.001 
            AND abs(cast(${zoneAssessments.longitude} as numeric) - ${parseFloat(assessment.longitude)}) < 0.001`
      );

    if (existingZones.length > 0) {
      return existingZones[0]; // Return existing zone
    }

    const [newAssessment] = await db
      .insert(zoneAssessments)
      .values({
        latitude: assessment.latitude,
        longitude: assessment.longitude,
        radius: assessment.radius || 1000,
        averageSeverity: assessment.averageSeverity || 'unknown',
        reportCount: assessment.reportCount || 0,
        lastUpdated: assessment.lastUpdated || new Date(),
      })
      .returning();

    return newAssessment;
  }
}

export const storage = new DatabaseStorage();

