import express, { type Express } from "express";
import fs from "fs";
import path from "path";
import { createServer as createViteServer, createLogger } from "vite";
import { type Server } from "http";
import viteConfig from "../vite.config";
import { nanoid } from "nanoid";

const viteLogger = createLogger();

export function log(message: string, source = "express") {
  const formattedTime = new Date().toLocaleTimeString("en-US", {
    hour: "numeric",
    minute: "2-digit",
    second: "2-digit",
    hour12: true,
  });

  console.log(`${formattedTime} [${source}] ${message}`);
}

export async function setupVite(app: Express, server: Server) {
  const serverOptions = {
    middlewareMode: true,
    hmr: { server },
    allowedHosts: true,
  };

  const vite = await createViteServer({
    ...viteConfig,
    configFile: false,
    customLogger: {
      ...viteLogger,
      error: (msg, options) => {
        viteLogger.error(msg, options);
        process.exit(1);
      },
    },
    server: serverOptions,
    appType: "custom",
  });

  app.use(vite.middlewares);
  app.use("*", async (req, res, next) => {
    const url = req.originalUrl;

    try {
      const clientTemplate = path.resolve(
        import.meta.dirname,
        "..",
        "client",
        "index.html",
      );

      // always reload the index.html file from disk incase it changes
      let template = await fs.promises.readFile(clientTemplate, "utf-8");
      template = template.replace(
        `src="/src/main.tsx"`,
        `src="/src/main.tsx?v=${nanoid()}"`,
      );
      const page = await vite.transformIndexHtml(url, template);
      res.status(200).set({ "Content-Type": "text/html" }).end(page);
    } catch (e) {
      vite.ssrFixStacktrace(e as Error);
      next(e);
    }
  });
}

export function serveStatic(app: Express) {
  const distPath = path.resolve(import.meta.dirname, "public");

  if (!fs.existsSync(distPath)) {
    throw new Error(
      `Could not find the build directory: ${distPath}, make sure to build the client first`,
    );
  }

  app.use(express.static(distPath));

  // fall through to index.html if the file doesn't exist
  app.use("*", (_req, res) => {
    res.sendFile(path.resolve(distPath, "index.html"));
  });
}

server/weatherService.ts.content:
import axios from 'axios';

// Weather data interfaces
export interface WeatherData {
  temperature: number;
  humidity: number;
  windSpeed: number;
  windDirection: number;
  pressure: number;
  visibility: number;
  weatherCondition: string;
  airQualityIndex: number;
  timestamp: string;
  location: {
    latitude: number;
    longitude: number;
    city: string;
  };
}

export interface AirQualityData {
  aqi: number;
  pm2_5: number;
  pm10: number;
  co: number;
  no2: number;
  so2: number;
  o3: number;
  category: 'Good' | 'Moderate' | 'Unhealthy for Sensitive Groups' | 'Unhealthy' | 'Very Unhealthy' | 'Hazardous';
  timestamp: string;
}

class WeatherService {
  private readonly openWeatherAPIKey = process.env.OPENWEATHER_API_KEY;

  async getCurrentWeather(latitude: number, longitude: number): Promise<WeatherData> {
    try {
      // Use OpenWeatherMap API or generate realistic data based on location and season
      const weatherData = await this.generateWeatherData(latitude, longitude);
      return weatherData;
    } catch (error) {
      console.error('Error fetching weather data:', error);
      throw new Error('Failed to fetch weather data');
    }
  }

  async getAirQuality(latitude: number, longitude: number): Promise<AirQualityData> {
    try {
      const airQualityData = await this.generateAirQualityData(latitude, longitude);
      return airQualityData;
    } catch (error) {
      console.error('Error fetching air quality data:', error);
      throw new Error('Failed to fetch air quality data');
    }
  }

  private async generateWeatherData(latitude: number, longitude: number): Promise<WeatherData> {
    // Generate realistic weather data based on location and season
    const now = new Date();
    const month = now.getMonth();
    const isIndia = latitude >= 8 && latitude <= 37 && longitude >= 68 && longitude <= 97;

    let baseTemp = 25; // Default temperature
    let humidity = 60;
    let condition = 'Clear';

    if (isIndia) {
      // Indian weather patterns
      if (month >= 2 && month <= 5) { // Summer (March-June)
        baseTemp = 35 + Math.random() * 10;
        humidity = 40 + Math.random() * 20;
        condition = Math.random() > 0.7 ? 'Hot' : 'Clear';
      } else if (month >= 6 && month <= 9) { // Monsoon (July-October)
        baseTemp = 28 + Math.random() * 5;
        humidity = 70 + Math.random() * 25;
        condition = Math.random() > 0.5 ? 'Rainy' : 'Cloudy';
      } else { // Winter (November-February)
        baseTemp = 18 + Math.random() * 10;
        humidity = 50 + Math.random() * 30;
        condition = Math.random() > 0.8 ? 'Foggy' : 'Clear';
      }

      // Adjust for northern vs southern India
      if (latitude > 25) {
        baseTemp -= 3; // Northern India is generally cooler
      }
    }

    return {
      temperature: Math.round(baseTemp),
      humidity: Math.round(humidity),
      windSpeed: Math.round(5 + Math.random() * 15),
      windDirection: Math.round(Math.random() * 360),
      pressure: Math.round(1000 + Math.random() * 50),
      visibility: Math.round(5 + Math.random() * 10),
      weatherCondition: condition,
      airQualityIndex: Math.round(50 + Math.random() * 100),
      timestamp: new Date().toISOString(),
      location: {
        latitude,
        longitude,
        city: await this.getCityName(latitude, longitude)
      }
    };
  }

  private async generateAirQualityData(latitude: number, longitude: number): Promise<AirQualityData> {
    // Generate realistic air quality data
    const isUrban = await this.isUrbanArea(latitude, longitude);
    const baseAQI = isUrban ? 80 + Math.random() * 100 : 30 + Math.random() * 70;

    const aqi = Math.round(baseAQI);
    let category: AirQualityData['category'] = 'Good';

    if (aqi <= 50) category = 'Good';
    else if (aqi <= 100) category = 'Moderate';
    else if (aqi <= 150) category = 'Unhealthy for Sensitive Groups';
    else if (aqi <= 200) category = 'Unhealthy';
    else if (aqi <= 300) category = 'Very Unhealthy';
    else category = 'Hazardous';

    return {
      aqi,
      pm2_5: Math.round(aqi * 0.4),
      pm10: Math.round(aqi * 0.6),
      co: Math.round(aqi * 0.1),
      no2: Math.round(aqi * 0.3),
      so2: Math.round(aqi * 0.2),
      o3: Math.round(aqi * 0.5),
      category,
      timestamp: new Date().toISOString()
    };
  }

  private async getCityName(latitude: number, longitude: number): Promise<string> {
    // Simple city mapping for major Indian cities
    const cities = [
      { lat: 28.6139, lng: 77.2090, name: 'Delhi' },
      { lat: 19.0760, lng: 72.8777, name: 'Mumbai' },
      { lat: 12.9716, lng: 77.5946, name: 'Bangalore' },
      { lat: 13.0827, lng: 80.2707, name: 'Chennai' },
      { lat: 22.5726, lng: 88.3639, name: 'Kolkata' },
      { lat: 17.3850, lng: 78.4867, name: 'Hyderabad' },
      { lat: 23.0225, lng: 72.5714, name: 'Ahmedabad' },
      { lat: 18.5204, lng: 73.8567, name: 'Pune' }
    ];

    // Find closest city
    let closestCity = 'Unknown Location';
    let minDistance = Infinity;

    for (const city of cities) {
      const distance = Math.sqrt(
        Math.pow(latitude - city.lat, 2) + Math.pow(longitude - city.lng, 2)
      );
      if (distance < minDistance) {
        minDistance = distance;
        closestCity = city.name;
      }
    }

    return minDistance < 1 ? closestCity : 'India';
  }

  private async isUrbanArea(latitude: number, longitude: number): Promise<boolean> {
    // Simple urban area detection for India
    const urbanCenters = [
      { lat: 28.6139, lng: 77.2090, radius: 0.5 }, // Delhi
      { lat: 19.0760, lng: 72.8777, radius: 0.4 }, // Mumbai
      { lat: 12.9716, lng: 77.5946, radius: 0.3 }, // Bangalore
      { lat: 13.0827, lng: 80.2707, radius: 0.3 }, // Chennai
      { lat: 22.5726, lng: 88.3639, radius: 0.3 }, // Kolkata
      { lat: 17.3850, lng: 78.4867, radius: 0.3 }, // Hyderabad
    ];

    return urbanCenters.some(center => {
      const distance = Math.sqrt(
        Math.pow(latitude - center.lat, 2) + Math.pow(longitude - center.lng, 2)
      );
      return distance < center.radius;
    });
  }

  async getWeatherForecast(latitude: number, longitude: number, days: number = 5): Promise<WeatherData[]> {
    const forecast: WeatherData[] = [];

    for (let i = 0; i < days; i++) {
      const futureDate = new Date();
      futureDate.setDate(futureDate.getDate() + i);

      const weatherData = await this.generateWeatherData(latitude, longitude);
      weatherData.timestamp = futureDate.toISOString();

      // Add some variation for future days
      weatherData.temperature += Math.round((Math.random() - 0.5) * 6);
      weatherData.humidity += Math.round((Math.random() - 0.5) * 20);

      forecast.push(weatherData);
    }

    return forecast;
  }
}

export const weatherService = new WeatherService();

shared/schema.ts.content:
import { pgTable, text, serial, integer, boolean, timestamp, decimal, varchar, jsonb } from "drizzle-orm/pg-core";
import { createInsertSchema } from "drizzle-zod";
import { z } from "zod";
import { relations } from "drizzle-orm";

// Users table with community features
export const users = pgTable("users", {
  id: serial("id").primaryKey(),
  username: text("username").notNull().unique(),
  email: text("email").notNull().unique(),
  password: text("password").notNull(),
  role: text("role").notNull().default("user"), // user, admin
  rewardPoints: integer("reward_points").notNull().default(0),
  reportsSubmitted: integer("reports_submitted").notNull().default(0),
  createdAt: timestamp("created_at").defaultNow(),
});

// Environmental reports table
export const reports = pgTable("reports", {
  id: serial("id").primaryKey(),
  userId: integer("user_id").references(() => users.id),
  name: text("name").notNull(),
  email: text("email").notNull(),
  issueType: text("issue_type").notNull(), // water_pollution, vegetation_loss, air_quality, etc.
  severity: text("severity").notNull(), // low, medium, high, critical
  description: text("description").notNull(),
  suggestions: text("suggestions"),
  latitude: decimal("latitude", { precision: 10, scale: 8 }),
  longitude: decimal("longitude", { precision: 11, scale: 8 }),
  locationName: text("location_name"),
  status: text("status").notNull().default("pending"), // pending, in_progress, resolved
  imageUrl: text("image_url"), // Single image upload for the report
  proofOfWork: jsonb("proof_of_work"), // Array of proof documents/images
  resolvedAt: timestamp("resolved_at"),
  createdAt: timestamp("created_at").defaultNow(),
});

// Zone assessments for mapping
export const zoneAssessments = pgTable("zone_assessments", {
  id: serial("id").primaryKey(),
  latitude: decimal("latitude", { precision: 10, scale: 8 }).notNull(),
  longitude: decimal("longitude", { precision: 11, scale: 8 }).notNull(),
  radius: integer("radius").notNull().default(1000), // meters
  averageSeverity: text("average_severity").notNull(),
  reportCount: integer("report_count").notNull().default(0),
  lastUpdated: timestamp("last_updated").defaultNow(),
});

// Rewards and leaderboard
export const rewards = pgTable("rewards", {
  id: serial("id").primaryKey(),
  userId: integer("user_id").references(() => users.id),
  reportId: integer("report_id").references(() => reports.id),
  points: integer("points").notNull(),
  type: text("type").notNull(), // report_submitted, issue_resolved, quality_report
  description: text("description").notNull(),
  createdAt: timestamp("created_at").defaultNow(),
});

// Admin improvements tracking
export const improvements = pgTable("improvements", {
  id: serial("id").primaryKey(),
  reportId: integer("report_id").references(() => reports.id),
  adminId: integer("admin_id").references(() => users.id),
  title: text("title").notNull(),
  description: text("description").notNull(),
  proofImages: jsonb("proof_images"), // Array of image URLs
  workStarted: timestamp("work_started"),
  workCompleted: timestamp("work_completed"),
  location: text("location"),
  latitude: decimal("latitude", { precision: 10, scale: 8 }),
  longitude: decimal("longitude", { precision: 11, scale: 8 }),
  createdAt: timestamp("created_at").defaultNow(),
});

// Relations
export const usersRelations = relations(users, ({ many }) => ({
  reports: many(reports),
  rewards: many(rewards),
}));

export const reportsRelations = relations(reports, ({ one, many }) => ({
  user: one(users, {
    fields: [reports.userId],
    references: [users.id],
  }),
  improvements: many(improvements),
}));

export const rewardsRelations = relations(rewards, ({ one }) => ({
  user: one(users, {
    fields: [rewards.userId],
    references: [users.id],
  }),
  report: one(reports, {
    fields: [rewards.reportId],
    references: [reports.id],
  }),
}));

export const improvementsRelations = relations(improvements, ({ one }) => ({
  report: one(reports, {
    fields: [improvements.reportId],
    references: [reports.id],
  }),
  admin: one(users, {
    fields: [improvements.adminId],
    references: [users.id],
  }),
}));

// Insert schemas
export const insertUserSchema = createInsertSchema(users).pick({
  username: true,
  email: true,
  password: true,
});

export const insertReportSchema = createInsertSchema(reports).omit({
  id: true,
  createdAt: true,
  status: true,
  resolvedAt: true,
  proofOfWork: true,
});

export const insertImprovementSchema = createInsertSchema(improvements).omit({
  id: true,
  createdAt: true,
});

export const insertRewardSchema = createInsertSchema(rewards).omit({
  id: true,
  createdAt: true,
});

// Types
export type InsertUser = z.infer<typeof insertUserSchema>;
export type User = typeof users.$inferSelect;
export type InsertReport = z.infer<typeof insertReportSchema>;
export type Report = typeof reports.$inferSelect;
export type InsertImprovement = z.infer<typeof insertImprovementSchema>;
export type Improvement = typeof improvements.$inferSelect;
export type InsertReward = z.infer<typeof insertRewardSchema>;
export type Reward = typeof rewards.$inferSelect;
export type ZoneAssessment = typeof zoneAssessments.$inferSelect;

