import React, { useState, useCallback } from 'react';
import { Upload, X, Image as ImageIcon, AlertCircle } from 'lucide-react';
import { Button } from '@/components/ui/enhanced-button';
import { Card, CardContent } from '@/components/ui/card';
import { Progress } from '@/components/ui/progress';
import { Alert, AlertDescription } from '@/components/ui/alert';

export interface UploadedImage {
  filename: string;
  originalName: string;
  size: number;
  mimeType: string;
  url: string;
}

interface ImageUploadProps {
  onImageUploaded: (image: UploadedImage) => void;
  onImageRemoved: (filename: string) => void;
  maxImages?: number;
  uploadedImages?: UploadedImage[];
  disabled?: boolean;
}

const ImageUpload: React.FC<ImageUploadProps> = ({
  onImageUploaded,
  onImageRemoved,
  maxImages = 3,
  uploadedImages = [],
  disabled = false
}) => {
  const [dragActive, setDragActive] = useState(false);
  const [uploading, setUploading] = useState(false);
  const [uploadProgress, setUploadProgress] = useState(0);
  const [error, setError] = useState<string | null>(null);

  const uploadImage = async (file: File) => {
    const formData = new FormData();
    formData.append('image', file);

    try {
      setUploading(true);
      setUploadProgress(0);
      setError(null);

      // Simulate progress
      const progressInterval = setInterval(() => {
        setUploadProgress(prev => Math.min(prev + 10, 90));
      }, 100);

      const response = await fetch('/api/upload-image', {
        method: 'POST',
        body: formData,
      });

      clearInterval(progressInterval);
      setUploadProgress(100);

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Upload failed');
      }

      const result = await response.json();

      if (result.success) {
        onImageUploaded(result.image);
      } else {
        throw new Error(result.message || 'Upload failed');
      }
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Upload failed');
    } finally {
      setUploading(false);
      setUploadProgress(0);
      setTimeout(() => setError(null), 5000);
    }
  };

  const handleDrag = useCallback((e: React.DragEvent) => {
    e.preventDefault();
    e.stopPropagation();
    if (e.type === 'dragenter' || e.type === 'dragover') {
      setDragActive(true);
    } else if (e.type === 'dragleave') {
      setDragActive(false);
    }
  }, []);

  const handleDrop = useCallback(
    (e: React.DragEvent) => {
      e.preventDefault();
      e.stopPropagation();
      setDragActive(false);

      if (disabled || uploadedImages.length >= maxImages) return;

      const files = Array.from(e.dataTransfer.files);
      const imageFiles = files.filter(file => file.type.startsWith('image/'));

      if (imageFiles.length > 0) {
        const remainingSlots = maxImages - uploadedImages.length;
        const filesToUpload = imageFiles.slice(0, remainingSlots);

        filesToUpload.forEach(file => {
          if (file.size > 10 * 1024 * 1024) {
            setError('Image size must be less than 10MB');
            return;
          }
          uploadImage(file);
        });
      }
    },
    [disabled, uploadedImages.length, maxImages]
  );

  const handleFileSelect = (e: React.ChangeEvent<HTMLInputElement>) => {
    const files = Array.from(e.target.files || []);
    const imageFiles = files.filter(file => file.type.startsWith('image/'));

    if (imageFiles.length > 0) {
      const remainingSlots = maxImages - uploadedImages.length;
      const filesToUpload = imageFiles.slice(0, remainingSlots);

      filesToUpload.forEach(file => {
        if (file.size > 10 * 1024 * 1024) {
          setError('Image size must be less than 10MB');
          return;
        }
        uploadImage(file);
      });
    }

    // Reset input
    e.target.value = '';
  };

  const canUploadMore = uploadedImages.length < maxImages && !disabled;

  return (
    <div className="space-y-4">
      {/* Upload Zone */}
      {canUploadMore && (
        <Card className="border-2 border-dashed hover:border-primary/50 transition-colors">
          <CardContent className="p-6">
            <div
              className={`relative cursor-pointer transition-colors ${
                dragActive ? 'bg-primary/5' : ''
              } ${uploading ? 'pointer-events-none opacity-50' : ''}`}
              onDragEnter={handleDrag}
              onDragLeave={handleDrag}
              onDragOver={handleDrag}
              onDrop={handleDrop}
            >
              <input
                type="file"
                multiple
                accept="image/*"
                onChange={handleFileSelect}
                className="absolute inset-0 w-full h-full opacity-0 cursor-pointer"
                disabled={uploading || disabled}
                data-testid="image-upload-input"
              />

              <div className="flex flex-col items-center text-center py-8">
                <Upload className="h-12 w-12 text-muted-foreground mb-4" />
                <h3 className="text-lg font-semibold mb-2">Upload Images</h3>
                <p className="text-sm text-muted-foreground mb-4">
                  Drag and drop images here or click to browse
                </p>
                <p className="text-xs text-muted-foreground">
                  PNG, JPG, GIF up to 10MB • {uploadedImages.length}/{maxImages} images
                </p>

                {uploading && (
                  <div className="w-full max-w-xs mt-4">
                    <Progress value={uploadProgress} className="h-2" />
                    <p className="text-xs text-center mt-2 text-muted-foreground">
                      Uploading... {uploadProgress}%
                    </p>
                  </div>
                )}
              </div>
            </div>
          </CardContent>
        </Card>
      )}

      {/* Error Message */}
      {error && (
        <Alert variant="destructive">
          <AlertCircle className="h-4 w-4" />
          <AlertDescription>{error}</AlertDescription>
        </Alert>
      )}

      {/* Uploaded Images */}
      {uploadedImages.length > 0 && (
        <div className="grid grid-cols-2 md:grid-cols-3 gap-4">
          {uploadedImages.map((image, index) => (
            <Card key={image.filename} className="relative overflow-hidden">
              <CardContent className="p-0">
                <div className="aspect-square relative bg-gray-100">
                  <img
                    src={image.url}
                    alt={image.originalName}
                    className="w-full h-full object-cover"
                    data-testid={`uploaded-image-${index}`}
                  />
                  <div className="absolute inset-0 bg-black/20 opacity-0 hover:opacity-100 transition-opacity">
                    <div className="absolute top-2 right-2">
                      <Button
                        size="sm"
                        variant="destructive"
                        onClick={() => onImageRemoved(image.filename)}
                        className="h-8 w-8 p-0"
                        data-testid={`remove-image-${index}`}
                      >
                        <X className="h-4 w-4" />
                      </Button>
                    </div>
                  </div>
                </div>
                <div className="p-3">
                  <p className="text-sm font-medium truncate" title={image.originalName}>
                    {image.originalName}
                  </p>
                  <p className="text-xs text-muted-foreground">
                    {(image.size / 1024 / 1024).toFixed(1)} MB
                  </p>
                </div>
              </CardContent>
            </Card>
          ))}
        </div>
      )}

      {/* Upload More Button */}
      {uploadedImages.length > 0 && canUploadMore && (
        <Button
          variant="outline"
          className="w-full"
          onClick={() => document.querySelector<HTMLInputElement>('[data-testid="image-upload-input"]')?.click()}
          disabled={uploading}
          data-testid="upload-more-button"
        >
          <ImageIcon className="h-4 w-4 mr-2" />
          Add More Images ({uploadedImages.length}/{maxImages})
        </Button>
      )}
    </div>
  );
};

export default ImageUpload;

client/src/hooks/use-mobile.tsx.content:
import * as React from "react"

const MOBILE_BREAKPOINT = 768

export function useIsMobile() {
  const [isMobile, setIsMobile] = React.useState<boolean | undefined>(undefined)

  React.useEffect(() => {
    const mql = window.matchMedia(`(max-width: ${MOBILE_BREAKPOINT - 1}px)`)
    const onChange = () => {
      setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    }
    mql.addEventListener("change", onChange)
    setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    return () => mql.removeEventListener("change", onChange)
  }, [])

  return !!isMobile
}

