client/src/pages/ReportIssue.tsx.content:
import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { z } from "zod";
import Header from "@/components/layout/Header";
import Footer from "@/components/layout/Footer";
import { Button } from "@/components/ui/enhanced-button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Form, FormControl, FormDescription, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { toast } from "sonner";
import { apiRequest } from "@/lib/queryClient";
import { ObjectUploader } from "@/components/ObjectUploader";
import { MapPin, Camera, Award, Send, CheckCircle2, Upload } from "lucide-react";

const reportSchema = z.object({
  name: z.string().min(2, "Name must be at least 2 characters"),
  email: z.string().email("Please enter a valid email address"),
  issueType: z.string().min(1, "Please select an issue type"),
  severity: z.string().min(1, "Please select severity level"),
  description: z.string().min(20, "Description must be at least 20 characters"),
  suggestions: z.string().optional(),
  latitude: z.string().optional(),
  longitude: z.string().optional(),
  locationName: z.string().optional(),
  imageUrl: z.string().optional(),
});

type ReportFormData = z.infer<typeof reportSchema>;

const ReportIssue = () => {
  const [isSubmitted, setIsSubmitted] = useState(false);
  const [isGettingLocation, setIsGettingLocation] = useState(false);
  const [uploadedImageUrl, setUploadedImageUrl] = useState<string>("");
  // Toast is imported directly from sonner
  const queryClient = useQueryClient();

  const form = useForm<ReportFormData>({
    resolver: zodResolver(reportSchema),
    defaultValues: {
      name: "",
      email: "",
      issueType: "",
      severity: "",
      description: "",
      suggestions: "",
      latitude: "",
      longitude: "",
      locationName: "",
      imageUrl: "",
    },
  });

  const reportMutation = useMutation({
    mutationFn: (data: ReportFormData) => 
      apiRequest("/api/reports", {
        method: "POST",
        body: JSON.stringify({
          ...data,
          imageUrl: uploadedImageUrl || data.imageUrl,
        }),
      }),
    onSuccess: (response) => {
      setIsSubmitted(true);
      queryClient.invalidateQueries({ queryKey: ["/api/reports"] });
      queryClient.invalidateQueries({ queryKey: ["/api/leaderboard"] });
      toast({
        title: "Report Submitted Successfully!",
        description: response.message || "Thank you for contributing to environmental monitoring!",
      });
    },
    onError: (error) => {
      toast({
        title: "Error",
        description: "Failed to submit report. Please try again.",
        variant: "destructive",
      });
    },
  });

  const getCurrentLocation = () => {
    setIsGettingLocation(true);
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const { latitude, longitude } = position.coords;
          form.setValue("latitude", latitude.toString());
          form.setValue("longitude", longitude.toString());
          form.setValue("locationName", `${latitude.toFixed(6)}, ${longitude.toFixed(6)}`);
          setIsGettingLocation(false);
          toast({
            title: "Location Captured",
            description: "Your current location has been added to the report.",
          });
        },
        (error) => {
          setIsGettingLocation(false);
          toast({
            title: "Location Error",
            description: "Unable to get your location. Please enter manually if needed.",
            variant: "destructive",
          });
        }
      );
    } else {
      setIsGettingLocation(false);
      toast({
        title: "Location Not Supported",
        description: "Geolocation is not supported by this browser.",
        variant: "destructive",
      });
    }
  };

  const onSubmit = (data: ReportFormData) => {
    reportMutation.mutate({
      ...data,
      imageUrl: uploadedImageUrl || data.imageUrl,
    });
  };

  // Handle image upload
  const handleGetUploadParameters = async () => {
    const response = await apiRequest("/api/objects/upload", { method: "POST" });
    return {
      method: "PUT" as const,
      url: response.uploadURL,
    };
  };

  const handleUploadComplete = (result: any) => {
    if (result.successful && result.successful.length > 0) {
      const uploadedFile = result.successful[0];
      // Extract object path from the upload URL
      const uploadURL = uploadedFile.uploadURL || "";
      if (uploadURL.includes("/uploads/")) {
        const objectId = uploadURL.split("/uploads/")[1].split("?")[0];
        const normalizedPath = `/objects/uploads/${objectId}`;
        setUploadedImageUrl(normalizedPath);
        form.setValue("imageUrl", normalizedPath);
        toast({
          title: "Image Uploaded",
          description: "Your image has been uploaded successfully!",
        });
      }
    }
  };

  const issueTypes = [
    { value: "water_pollution", label: "Water Pollution" },
    { value: "vegetation_loss", label: "Vegetation Loss" },
    { value: "soil_erosion", label: "Soil Erosion" },
    { value: "air_quality", label: "Air Quality Issues" },
    { value: "waste_disposal", label: "Improper Waste Disposal" },
    { value: "deforestation", label: "Deforestation" },
    { value: "wildlife_disturbance", label: "Wildlife Disturbance" },
    { value: "other", label: "Other Environmental Issue" },
  ];

  const severityLevels = [
    { value: "low", label: "Low - Minor concern" },
    { value: "medium", label: "Medium - Moderate impact" },
    { value: "high", label: "High - Significant concern" },
    { value: "critical", label: "Critical - Immediate action needed" },
  ];

  if (isSubmitted) {
    return (
      <div className="min-h-screen">
        <Header />
        <main className="py-20">
          <div className="container mx-auto px-4">
            <Card className="max-w-2xl mx-auto text-center bg-card/80 backdrop-blur-sm border-2 border-secondary/20">
              <CardHeader>
                <div className="mx-auto mb-4 h-16 w-16 bg-secondary rounded-full flex items-center justify-center">
                  <CheckCircle2 className="h-8 w-8 text-secondary-foreground" />
                </div>
                <CardTitle className="text-3xl text-foreground">Report Submitted Successfully!</CardTitle>
                <CardDescription className="text-lg">
                  Thank you for contributing to environmental monitoring. Your report helps make our communities healthier.
                </CardDescription>
              </CardHeader>
              <CardContent className="space-y-6">
                <div className="bg-gradient-to-r from-secondary/20 to-accent/20 rounded-lg p-6">
                  <div className="flex items-center justify-center gap-3 mb-3">
                    <Award className="h-6 w-6 text-secondary" />
                    <span className="text-xl font-semibold">+10 Points Earned!</span>
                  </div>
                  <p className="text-muted-foreground">
                    You've earned reward points for submitting this report. Check the leaderboard to see your ranking!
                  </p>
                </div>
                <div className="flex gap-4 justify-center">
                  <Button 
                    variant="secondary" 
                    onClick={() => setIsSubmitted(false)}
                    data-testid="button-submit-another"
                  >
                    Submit Another Report
                  </Button>
                  <Button 
                    variant="outline" 
                    onClick={() => window.location.href = "/leaderboard"}
                    data-testid="button-view-leaderboard"
                  >
                    View Leaderboard
                  </Button>
                </div>
              </CardContent>
            </Card>
          </div>
        </main>
        <Footer />
      </div>
    );
  }

  return (
    <div className="min-h-screen">
      <Header />

      <main className="py-8">
        <div className="container mx-auto px-4">
          {/* Page Header */}
          <div className="mb-8 text-center">
            <h1 className="text-4xl font-bold text-foreground mb-4">
              Report Environmental Issue
            </h1>
            <p className="text-lg text-muted-foreground max-w-2xl mx-auto">
              Help us monitor and protect our environment by reporting issues in your area. 
              Your contribution makes a difference and earns you reward points!
            </p>
          </div>

          {/* Incentive Cards */}
          <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8 max-w-4xl mx-auto">
            <Card className="bg-gradient-to-br from-secondary/20 to-secondary/10 border-secondary/20">
              <CardContent className="p-6 text-center">
                <Award className="h-8 w-8 text-secondary mx-auto mb-3" />
                <h3 className="font-semibold mb-2">Earn Rewards</h3>
                <p className="text-sm text-muted-foreground">Get 10 points for each report submitted</p>
              </CardContent>
            </Card>
            <Card className="bg-gradient-to-br from-accent/20 to-accent/10 border-accent/20">
              <CardContent className="p-6 text-center">
                <MapPin className="h-8 w-8 text-accent mx-auto mb-3" />
                <h3 className="font-semibold mb-2">Make Impact</h3>
                <p className="text-sm text-muted-foreground">Your reports help officials take action</p>
              </CardContent>
            </Card>
            <Card className="bg-gradient-to-br from-primary/20 to-primary/10 border-primary/20">
              <CardContent className="p-6 text-center">
                <Camera className="h-8 w-8 text-primary mx-auto mb-3" />
                <h3 className="font-semibold mb-2">Track Progress</h3>
                <p className="text-sm text-muted-foreground">See improvements made in your area</p>
              </CardContent>
            </Card>
          </div>

          {/* Report Form */}
          <Card className="max-w-2xl mx-auto bg-card/80 backdrop-blur-sm">
            <CardHeader>
              <CardTitle className="text-2xl">Submit Environmental Report</CardTitle>
              <CardDescription>
                Provide detailed information about the environmental issue you've observed
              </CardDescription>
            </CardHeader>
            <CardContent>
              <Form {...form}>
                <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-6">
                  {/* Personal Information */}
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    <FormField
                      control={form.control}
                      name="name"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Full Name</FormLabel>
                          <FormControl>
                            <Input placeholder="Your full name" {...field} data-testid="input-name" />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />
                    <FormField
                      control={form.control}
                      name="email"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Email Address</FormLabel>
                          <FormControl>
                            <Input placeholder="your.email@example.com" type="email" {...field} data-testid="input-email" />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />
                  </div>

                  {/* Issue Details */}
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    <FormField
                      control={form.control}
                      name="issueType"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Issue Type</FormLabel>
                          <Select onValueChange={field.onChange} defaultValue={field.value}>
                            <FormControl>
                              <SelectTrigger data-testid="select-issue-type">
                                <SelectValue placeholder="Select issue type" />
                              </SelectTrigger>
                            </FormControl>
                            <SelectContent>
                              {issueTypes.map((type) => (
                                <SelectItem key={type.value} value={type.value}>
                                  {type.label}
                                </SelectItem>
                              ))}
                            </SelectContent>
                          </Select>
                          <FormMessage />
                        </FormItem>
                      )}
                    />
                    <FormField
                      control={form.control}
                      name="severity"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Severity Level</FormLabel>
                          <Select onValueChange={field.onChange} defaultValue={field.value}>
                            <FormControl>
                              <SelectTrigger data-testid="select-severity">
                                <SelectValue placeholder="Select severity" />
                              </SelectTrigger>
                            </FormControl>
                            <SelectContent>
                              {severityLevels.map((level) => (
                                <SelectItem key={level.value} value={level.value}>
                                  {level.label}
                                </SelectItem>
                              ))}
                            </SelectContent>
                          </Select>
                          <FormMessage />
                        </FormItem>
                      )}
                    />
                  </div>

                  {/* Location */}
                  <div className="space-y-4">
                    <div className="flex items-center justify-between">
                      <FormLabel>Location Information</FormLabel>
                      <Button
                        type="button"
                        variant="outline"
                        size="sm"
                        onClick={getCurrentLocation}
                        disabled={isGettingLocation}
                        className="gap-2"
                        data-testid="button-get-location"
                      >
                        <MapPin className="h-4 w-4" />
                        {isGettingLocation ? "Getting Location..." : "Use Current Location"}
                      </Button>
                    </div>
                    <FormField
                      control={form.control}
                      name="locationName"
                      render={({ field }) => (
                        <FormItem>
                          <FormControl>
                            <Input 
                              placeholder="Enter location name or address" 
                              {...field} 
                              data-testid="input-location"
                            />
                          </FormControl>
                          <FormDescription>
                            Provide a location name or use the button above to capture your current coordinates
                          </FormDescription>
                          <FormMessage />
                        </FormItem>
                      )}
                    />
                  </div>

                  {/* Description */}
                  <FormField
                    control={form.control}
                    name="description"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Issue Description</FormLabel>
                        <FormControl>
                          <Textarea
                            placeholder="Describe the environmental issue you observed in detail..."
                            className="min-h-[120px]"
                            {...field}
                            data-testid="textarea-description"
                          />
                        </FormControl>
                        <FormDescription>
                          Provide specific details about what you observed, when, and any immediate concerns
                        </FormDescription>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  {/* Image Upload */}
                  <div className="space-y-3">
                    <FormLabel>Add Photo (Optional)</FormLabel>
                    <div className="flex items-center gap-4">
                      <ObjectUploader
                        maxNumberOfFiles={1}
                        maxFileSize={10485760} // 10MB
                        onGetUploadParameters={handleGetUploadParameters}
                        onComplete={handleUploadComplete}
                        buttonClassName="gap-2"
                      >
                        <Upload className="h-4 w-4" />
                        {uploadedImageUrl ? "Change Image" : "Upload Photo"}
                      </ObjectUploader>
                      {uploadedImageUrl && (
                        <div className="flex items-center gap-2 text-sm text-green-600">
                          <CheckCircle2 className="h-4 w-4" />
                          Image uploaded successfully
                        </div>
                      )}
                    </div>
                    <p className="text-sm text-muted-foreground">
                      Upload a photo to help illustrate the environmental issue (JPG, PNG, max 10MB)
                    </p>
                  </div>

                  {/* Suggestions */}
                  <FormField
                    control={form.control}
                    name="suggestions"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Suggestions (Optional)</FormLabel>
                        <FormControl>
                          <Textarea
                            placeholder="Any suggestions for improvement or solutions..."
                            className="min-h-[80px]"
                            {...field}
                            data-testid="textarea-suggestions"
                          />
                        </FormControl>
                        <FormDescription>
                          Share any ideas you have for addressing this issue
                        </FormDescription>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  {/* Submit Button */}
                  <Button
                    type="submit"
                    className="w-full gap-2"
                    disabled={reportMutation.isPending}
                    data-testid="button-submit-report"
                  >
                    <Send className="h-4 w-4" />
                    {reportMutation.isPending ? "Submitting Report..." : "Submit Report"}
                  </Button>
                </form>
              </Form>
            </CardContent>
          </Card>
        </div>
      </main>

      <Footer />
    </div>
  );
};

export default ReportIssue;

client/src/components/layout/Header.tsx.content:
import { useState } from "react";
import { Button } from "@/components/ui/enhanced-button";
import { useLocation, Link } from "wouter";
import { Menu, X } from "lucide-react";
import Logo from "@/components/Logo";

const Header = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [location, navigate] = useLocation();

  const navItems = [
    { name: "Home", path: "/" },
    { name: "Map Analysis", path: "/map" },
    { name: "Data Insights", path: "/insights" },
    { name: "Report Issue", path: "/report" },
    { name: "About", path: "/about" },
    { name: "Leaderboard", path: "/leaderboard" },
    { name: "Admin Panel", path: "/admin" },
  ];

  const isActivePath = (path: string) => location === path;

  return (
    <header className="bg-card/80 backdrop-blur-sm border-b border-border sticky top-0 z-50">
      <div className="container mx-auto px-4 py-4">
        <div className="flex items-center justify-between">
          {/* Logo */}
          <div 
            className="cursor-pointer hover:opacity-80 transition-opacity"
            onClick={() => navigate("/")}
          >
            <Logo size="md" showText={true} />
          </div>

          {/* Desktop Navigation */}
          <nav className="hidden md:flex items-center gap-6">
            {navItems.map((item) => (
              <button
                key={item.path}
                onClick={() => navigate(item.path)}
                className={`px-3 py-2 rounded-md text-sm font-medium transition-all ${
                  isActivePath(item.path)
                    ? "bg-primary text-primary-foreground shadow-water"
                    : "text-muted-foreground hover:text-foreground hover:bg-accent"
                }`}
              >
                {item.name}
              </button>
            ))}
            <Button variant="hero" size="sm" className="ml-4">
              Get Started
            </Button>
          </nav>

          {/* Mobile Menu Button */}
          <button
            className="md:hidden p-2 rounded-md hover:bg-accent transition-colors"
            onClick={() => setIsMenuOpen(!isMenuOpen)}
          >
            {isMenuOpen ? <X className="h-5 w-5" /> : <Menu className="h-5 w-5" />}
          </button>
        </div>

        {/* Mobile Navigation */}
        {isMenuOpen && (
          <nav className="md:hidden mt-4 pb-4 border-t border-border pt-4">
            <div className="flex flex-col gap-2">
              {navItems.map((item) => (
                <button
                  key={item.path}
                  onClick={() => {
                    navigate(item.path);
                    setIsMenuOpen(false);
                  }}
                  className={`px-3 py-2 rounded-md text-sm font-medium text-left transition-all ${
                    isActivePath(item.path)
                      ? "bg-primary text-primary-foreground"
                      : "text-muted-foreground hover:text-foreground hover:bg-accent"
                  }`}
                >
                  {item.name}
                </button>
              ))}
              <Button variant="hero" size="sm" className="mt-2 self-start">
                Get Started
              </Button>
            </div>
          </nav>
        )}
      </div>
    </header>
  );
};

export default Header;

client/src/components/map/RealTimeMap.ts.content:
import { useState, useEffect, useMemo } from 'react';
import { useQuery, useMutation } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/enhanced-button";
import { MapPin, Satellite, RefreshCw, Activity, AlertTriangle, Eye } from 'lucide-react';
import type { Report } from "@shared/schema";
import { apiRequest } from "@/lib/queryClient";
import LeafletMap from "./LeafletMap";

interface NDVIData {
  latitude: number;
  longitude: number;
  ndvi: number;
  severity: 'low' | 'medium' | 'high' | 'critical';
  vegetationHealth: number;
  waterBodyHealth: number;
  soilMoisture: number;
}

interface SeverityZone extends NDVIData {
  id: number;
  reportCount: number;
  location?: string;
  lastUpdated: string;
}

const RealTimeMap = () => {
  const [selectedZone, setSelectedZone] = useState<SeverityZone | null>(null);
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [severityZones, setSeverityZones] = useState<SeverityZone[]>([]);

  // Fetch user reports
  const { data: reports = [], isLoading: reportsLoading } = useQuery<Report[]>({
    queryKey: ["/api/reports"],
  });

  // Mutation to analyze NDVI data
  const analyzeNDVIMutation = useMutation({
    mutationFn: async ({ latitude, longitude }: { latitude: number; longitude: number }) => {
      return await apiRequest(`/api/analyze-ndvi`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ latitude, longitude, radius: 1000 })
      });
    },
    onSuccess: (data: NDVIData, variables) => {
      // Add the analyzed zone to our severity zones
      const newZone: SeverityZone = {
        ...data,
        id: Date.now(),
        reportCount: reports.filter(r => 
          r.latitude && r.longitude &&
          Math.abs(Number(r.latitude) - variables.latitude) < 0.01 &&
          Math.abs(Number(r.longitude) - variables.longitude) < 0.01
        ).length,
        lastUpdated: new Date().toISOString()
      };

      setSeverityZones(prev => {
        const filtered = prev.filter(zone => 
          !(Math.abs(zone.latitude - variables.latitude) < 0.005 &&
            Math.abs(zone.longitude - variables.longitude) < 0.005)
        );
        return [...filtered, newZone];
      });
    }
  });

  // Process reports to create severity zones
  const processReportsForAnalysis = useMutation({
    mutationFn: async (reportLocations: Array<{ latitude: number; longitude: number }>) => {
      setIsAnalyzing(true);
      const analysisPromises = reportLocations.map(location =>
        apiRequest(`/api/analyze-ndvi`, {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ 
            latitude: location.latitude, 
            longitude: location.longitude,
            radius: 1500 
          })
        })
      );

      return await Promise.all(analysisPromises);
    },
    onSuccess: (analysisResults: NDVIData[]) => {
      const newZones: SeverityZone[] = analysisResults.map((data, index) => {
        const reportLocation = uniqueReportLocations[index];
        const reportsInArea = reports.filter(r =>
          r.latitude && r.longitude &&
          Math.abs(Number(r.latitude) - data.latitude) < 0.01 &&
          Math.abs(Number(r.longitude) - data.longitude) < 0.01
        );

        return {
          ...data,
          id: Date.now() + index,
          reportCount: reportsInArea.length,
          location: reportsInArea[0]?.description || undefined,
          lastUpdated: new Date().toISOString()
        };
      });

      setSeverityZones(newZones);
      setIsAnalyzing(false);
    },
    onError: () => {
      setIsAnalyzing(false);
    }
  });

  // Get unique report locations for analysis
  const uniqueReportLocations = useMemo(() => {
    const locations = new Map();
    reports.forEach(report => {
      if (report.latitude && report.longitude) {
        const key = `${Number(report.latitude).toFixed(3)},${Number(report.longitude).toFixed(3)}`;
        if (!locations.has(key)) {
          locations.set(key, {
            latitude: Number(report.latitude),
            longitude: Number(report.longitude)
          });
        }
      }
    });
    return Array.from(locations.values());
  }, [reports]);

  // Auto-analyze when reports load
  useEffect(() => {
    if (uniqueReportLocations.length > 0 && severityZones.length === 0 && !isAnalyzing) {
      processReportsForAnalysis.mutate(uniqueReportLocations);
    }
  }, [uniqueReportLocations.length, severityZones.length, isAnalyzing]);

  const getSeverityStats = () => {
    const stats = {
      low: severityZones.filter(z => z.severity === 'low').length,
      medium: severityZones.filter(z => z.severity === 'medium').length,
      high: severityZones.filter(z => z.severity === 'high').length,
      critical: severityZones.filter(z => z.severity === 'critical').length
    };
    return stats;
  };

  const getSeverityColor = (severity: string) => {
    switch (severity) {
      case 'low': return 'bg-green-500';
      case 'medium': return 'bg-yellow-500';
      case 'high': return 'bg-orange-500';
      case 'critical': return 'bg-red-500';
      default: return 'bg-gray-500';
    }
  };

  const getSeverityBorder = (severity: string) => {
    switch (severity) {
      case 'low': return 'border-green-500';
      case 'medium': return 'border-yellow-500';
      case 'high': return 'border-orange-500';
      case 'critical': return 'border-red-500';
      default: return 'border-gray-500';
    }
  };

  const refreshAnalysis = () => {
    if (uniqueReportLocations.length > 0) {
      processReportsForAnalysis.mutate(uniqueReportLocations);
    }
  };

  const handleMapClick = (e: React.MouseEvent<HTMLDivElement>) => {
    if (analyzeNDVIMutation.isPending) return;

    const rect = e.currentTarget.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;

    // Convert pixel coordinates to lat/lng (India region: lat 8-37, lng 68-97)
    const latitude = 37 - (y / rect.height) * 29; // 8-37 lat range for India
    const longitude = 68 + (x / rect.width) * 29; // 68-97 lng range for India

    // Validate coordinates are within India bounds
    if (latitude >= 8 && latitude <= 37 && longitude >= 68 && longitude <= 97) {
      analyzeNDVIMutation.mutate({ latitude, longitude });
    }
  };

  const stats = getSeverityStats();

  return (
    <Card className="h-[600px] bg-card/50 backdrop-blur-sm border-2 border-primary/20">
      <CardHeader>
        <div className="flex items-center justify-between">
          <div>
            <CardTitle className="flex items-center gap-2">
              <Satellite className="h-5 w-5" />
              Real-Time NDVI Analysis Map
            </CardTitle>
            <CardDescription>
              Click anywhere to analyze vegetation health • Based on user reports with Google Earth Engine data
            </CardDescription>
          </div>
          <div className="flex gap-2">
            <Button 
              size="sm" 
              variant="outline" 
              onClick={refreshAnalysis} 
              disabled={isAnalyzing || uniqueReportLocations.length === 0}
              data-testid="button-refresh-analysis"
            >
              <RefreshCw className={`h-4 w-4 ${isAnalyzing ? 'animate-spin' : ''}`} />
            </Button>
            <Button size="sm" variant="outline" data-testid="button-analysis-info">
              <Activity className="h-4 w-4" />
              {severityZones.length}
            </Button>
          </div>
        </div>

        {/* Analysis Stats */}
        <div className="flex gap-4 pt-2">
          <div className="flex items-center gap-1 text-sm">
            <MapPin className="h-3 w-3 text-primary" />
            <span>{severityZones.length} Analysis Points</span>
          </div>
          {severityZones.length > 0 && (
            <div className="flex items-center gap-1 text-sm">
              <Satellite className="h-3 w-3 text-secondary" />
              <span>Last Updated: {new Date(severityZones[severityZones.length - 1]?.lastUpdated).toLocaleTimeString()}</span>
            </div>
          )}
        </div>
      </CardHeader>

      <CardContent className="h-[480px] relative">
        {reportsLoading || isAnalyzing ? (
          <div className="w-full h-full bg-gradient-to-br from-primary/5 to-secondary/5 rounded-lg flex items-center justify-center">
            <div className="flex flex-col items-center gap-4">
              <div className="flex items-center gap-3">
                <RefreshCw className="h-6 w-6 animate-spin" />
                <span className="text-lg font-medium">
                  {reportsLoading ? 'Loading reports...' : 'Analyzing vegetation health...'}
                </span>
              </div>
              <p className="text-sm text-muted-foreground text-center max-w-md">
                Using Google Earth Engine to analyze NDVI data from satellite imagery
              </p>
              {isAnalyzing && (
                <div className="flex items-center gap-2 bg-card/90 rounded-lg px-3 py-2">
                  <Satellite className="h-4 w-4 text-primary" />
                  <span className="text-sm">Processing {uniqueReportLocations.length} locations</span>
                </div>
              )}
            </div>
          </div>
        ) : (
          <LeafletMap className="w-full h-full" />
        )}



            {/* Analysis Instructions */}
            {severityZones.length === 0 && !isAnalyzing && (
              <div className="absolute inset-0 bg-gradient-to-br from-primary/5 via-transparent to-secondary/5 rounded-lg flex items-center justify-center pointer-events-none">
                <div className="bg-card/95 backdrop-blur-sm rounded-xl p-8 shadow-xl border border-primary/20 max-w-md text-center animate-pulse-glow">
                  <div className="relative mb-4">
                    <MapPin className="h-12 w-12 mx-auto text-primary animate-bounce" />
                    <div className="absolute -top-1 -right-1 w-4 h-4 bg-green-500 rounded-full animate-ping"></div>
                  </div>
                  <h3 className="font-bold text-xl mb-3 text-foreground">Click to Analyze</h3>
                  <p className="text-sm text-muted-foreground mb-6 leading-relaxed">
                    Click anywhere on this map to get instant vegetation health analysis using 
                    real satellite data from Google Earth Engine
                  </p>
                  <div className="flex items-center justify-center gap-2 text-sm text-primary font-medium mb-4">
                    <Satellite className="h-5 w-5 animate-pulse" />
                    <span>Google Earth Engine Ready</span>
                  </div>
                  {uniqueReportLocations.length > 0 && (
                    <Button
                      size="sm"
                      onClick={refreshAnalysis}
                      className="pointer-events-auto"
                      variant="hero"
                      data-testid="button-start-analysis"
                    >
                      <Activity className="h-4 w-4" />
                      Analyze {uniqueReportLocations.length} Report Locations
                    </Button>
                  )}
                </div>
              </div>
            )}

            {/* Loading Analysis Overlay */}
            {analyzeNDVIMutation.isPending && (
              <div className="absolute inset-0 bg-black/20 backdrop-blur-sm rounded-lg flex items-center justify-center z-20">
                <div className="bg-card/95 backdrop-blur-sm rounded-xl p-6 border border-primary/20 shadow-xl text-center">
                  <RefreshCw className="h-10 w-10 text-primary mx-auto mb-4 animate-spin" />
                  <h3 className="font-semibold text-lg mb-2">Analyzing Location</h3>
                  <p className="text-sm text-muted-foreground mb-3">
                    Processing satellite NDVI data from Google Earth Engine...
                  </p>
                  <div className="flex items-center justify-center gap-2 text-xs text-primary">
                    <Satellite className="h-4 w-4 animate-pulse" />
                    <span>Connecting to satellite data</span>
                  </div>
                </div>
              </div>
            )}

      </CardContent>
    </Card>
  );
};

export default RealTimeMap;

